import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Form, Button, Col } from 'react-bootstrap';

export default function EmployeeForm({ setEmployees }) {
  const [name, setName] = useState('');
  const [id, setId] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newEmployee = {
      id: id,
      name: name,
      email: email
    };
    fetch('https://jsonplaceholder.typicode.com/users', {
      method: 'POST',
      body: JSON.stringify(newEmployee),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      },
    })
      .then(response => response.json())
      .then(data => {
        setEmployees(prevEmployees => [...prevEmployees, data]);
        setId('');
        setName('');
        setEmail('');
      })
      .catch(error => console.log(error));
  }

  return (
    <div>
      <div className="container my-4">
        <h1>Employee Form</h1>
        <Navbar bg="light" expand="lg">
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formId">
              <Form.Group controlId="formId" className="row">
                <Form.Label className="col-sm-2">ID</Form.Label>
                <Col sm={10}>
                  <Form.Control type="text" placeholder="Enter ID" value={id} onChange={e => setId(e.target.value)} />
                </Col>
              </Form.Group>
            </Form.Group>
            <Form.Group controlId="formName">
              <Form.Label>Name</Form.Label>
              <Form.Control type="text" placeholder="Enter name" value={name} onChange={e => setName(e.target.value)} />
            </Form.Group>
            <Form.Group controlId="formEmail">
              <Form.Label>Email</Form.Label>
              <Form.Control type="email" placeholder="Enter email" value={email} onChange={e => setEmail(e.target.value)} />
            </Form.Group>
            <br />
            <Button variant="primary" type="submit">
              Enter
            </Button>
          </Form>
        </Navbar>
      </div>
    </div>

  );
}
